import HelloWorld from '@/components/HelloWorld';
import PxAlert from '@/components/PxAlert';
import PxPaginacion from '@/components/PxPaginacion';
import PxDetalle from '@/components/PxDetalle';
import app from "@/app";
export default {
  name: 'App',
  components: {
    HelloWorld,
    PxAlert,
    PxPaginacion,
    PxDetalle
  },
  data() {
    return {
      // isLoading: false,
      asset: {},
      cant: 1,
      message: false,
      message_increment: false,
      text_increment: "es el ultimo pokemon de la lista. No se puede seguir hacia adelante. Seleccione el botón anterior.",
      text: "es el ultimo pokemon de la lista. No se puede seguir hacia atrás. Seleccione el botón siguiente.",
      loading: true
    };
  },
  created() {
    this.getCoin();
  },
  methods: {
    increment() {
      this.message = false;
      this.message_increment = false;
      this.cant = this.cant + 1;
      const imprimir = this.cant;
      console.log(imprimir);
      if (this.cant > 8) {
        this.cant = 8;
        this.message_increment = true;
      }
      if (this.cant > 0) {
        this.getCoin(this.cant);
      }
    },
    decrement() {
      this.message_increment = false;
      this.cant = this.cant - 1;
      const imprimir = this.cant;
      console.log(imprimir);
      if (this.cant < 1) {
        this.cant = 1;
        this.message = true;
      }
      if (this.cant > 0) {
        this.getCoin(this.cant);
      }
    },
    getCoin() {
      // this.isLoading = true;
      this.loading = true;
      // const id = this.$route.params.id;
      // var id=this.cant
      Promise.all([app.getPokemon(this.cant)]).then(([asset]) => {
        this.asset = asset;
        this.loading = false;
        // this.history = history;
      });
    }
  }
};
