const url_pokemon="https://pokeapi.co/api/v2/pokemon/1"

function getPokemons() {
    try {

        return fetch(url_pokemon)
            .then((res) => res.json())
            .then((res) => res.data);
    } 
    catch {
        console.log("Ocurrió un error")
    }
}
function getPokemon(coin) {
  return fetch(`${url_pokemon}/${coin}`)
    .then((res) => res.json())
    .then((res) => res.data);
}
getPokemons();
export default {
    getPokemons,
    getPokemon,
  };
